import React, { useState } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';

export default function Bookings() {
  const { scheduleId } = useParams();
  const [userId, setUserId] = useState(''); // for now, enter manually
  const [seats, setSeats] = useState(1);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleBook = async (e) => {
    e.preventDefault();
    setError(null);
    setResult(null);
    try {
      const res = await axios.post('/api/bookings', {
        user_id: userId ? Number(userId) : null,
        schedule_id: Number(scheduleId),
        seats: Number(seats)
      });
      setResult(res.data);
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.error || 'Booking failed');
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Book Tickets</h1>
      <form onSubmit={handleBook}>
        <div>
          <label>User ID (temp): </label>
          <input 
            value={userId} 
            onChange={e => setUserId(e.target.value)} 
            placeholder="1" 
            required 
          />
        </div>
        <div>
          <label>Seats: </label>
          <input 
            type="number" 
            value={seats} 
            min="1" 
            onChange={e => setSeats(e.target.value)} 
            required 
          />
        </div>
        <button type="submit" style={{ marginTop: 10 }}>Confirm Booking</button>
      </form>

      {result && (
        <div style={{ marginTop: 20, padding: 10, border: '1px solid green' }}>
          <h3>Booking Confirmed!</h3>
          <p>Booking ID: {result.booking_id}</p>
          <p>m-Ticket Code: <strong>{result.code}</strong></p>
          <p>Status: {result.status}</p>
        </div>
      )}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div style={{ marginTop: 20 }}>
        <Link to={`/schedules/${scheduleId}`}>‚Üê Back to Schedule</Link>
      </div>
    </div>
  );
}
