import React from 'react';
import { Link } from 'react-router-dom';

export default function Home(){
  return (
    <div style={{padding:20}}>
      <h1>Multiplex Booking — Home</h1>
      <p>Welcome! Use the navigation to view movies and book tickets.</p>

      <div style={{marginTop:20}}>
        <Link to="/movies">View Movies →</Link>
      </div>

      <hr style={{margin:'20px 0'}} />

      <section>
        <h3>Quick dev notes</h3>
        <ul>
          <li>The frontend expects a backend at <code>/api/*</code>.</li>
          <li>Movies list is fetched from <code>/api/movies</code> in the Movies page.</li>
          <li>If you want a homepage movie preview, I can add a featured-movie API next.</li>
        </ul>
      </section>
    </div>
  );
}
