import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Movies(){
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    let mounted = true;
    setLoading(true);
    axios.get('/api/movies')
      .then(res => {
        if(!mounted) return;
        setMovies(res.data || []);
      })
      .catch(err => {
        console.error(err);
        if(!mounted) return;
        setError(err.message || 'Failed to fetch');
      })
      .finally(() => mounted && setLoading(false));
    return () => { mounted = false; };
  }, []);

  return (
    <div style={{padding:20}}>
      <h1>Movies</h1>

      {loading && <p>Loading movies…</p>}
      {error && <p style={{color:'crimson'}}>Error: {error}</p>}

      {!loading && movies.length === 0 && (
        <p>No movies found. Ensure your backend `/api/movies` is running and returns JSON.</p>
      )}

      <ul>
        {movies.map(movie => (
          <li key={movie.id} style={{marginBottom:12}}>
            <strong>{movie.title}</strong> {movie.duration ? `— ${movie.duration} min` : ''}
            <div style={{fontSize:13, color:'#555'}}>
              {movie.language ? movie.language + ' • ' : ''}{movie.release_date ?? ''}
            </div>
            {/* If you implement booking route, link to it here */}
          </li>
        ))}
      </ul>
    </div>
  );
}
