import React, { useState, useEffect } from 'react';

// Mock data for demonstration (replace with actual API call)
const mockBookings = [
  {
    id: 1,
    movieTitle: "Spider-Man: No Way Home",
    posterUrl: "https://via.placeholder.com/300x450/1a1a2e/ffffff?text=Spider-Man",
    showDate: "2025-08-15",
    showTime: "7:30 PM",
    theater: "Screen 1",
    seats: ["A1", "A2"],
    totalAmount: 450,
    bookingDate: "2025-08-10",
    status: "confirmed",
    bookingCode: "SPD001",
    ticketCode: "TKT-SPD-001"
  },
  {
    id: 2,
    movieTitle: "The Batman",
    posterUrl: "https://via.placeholder.com/300x450/0f0f23/ffffff?text=Batman",
    showDate: "2025-08-20",
    showTime: "9:00 PM",
    theater: "Screen 2",
    seats: ["C5", "C6", "C7"],
    totalAmount: 675,
    bookingDate: "2025-08-08",
    status: "confirmed",
    bookingCode: "BAT002",
    ticketCode: "TKT-BAT-002"
  },
  {
    id: 3,
    movieTitle: "Dune: Part Two",
    posterUrl: "https://via.placeholder.com/300x450/2d1b69/ffffff?text=Dune",
    showDate: "2025-08-12",
    showTime: "2:00 PM",
    theater: "Screen 3",
    seats: ["B10"],
    totalAmount: 225,
    bookingDate: "2025-08-05",
    status: "expired",
    bookingCode: "DUN003",
    ticketCode: "TKT-DUN-003"
  }
];

export default function MyBookings() {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedBooking, setSelectedBooking] = useState(null);

  useEffect(() => {
    fetchBookings();
  }, []);

  const fetchBookings = async () => {
    try {
      setLoading(true);
    //   In real implementation, replace with actual API call
       const response = await fetch('/api/my-bookings/1', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json',
          },
        });
      const data = await response.json();
      
      // Simulating API delay
      await new Promise(resolve => setTimeout(resolve, 1000));
      setBookings(data);
      setLoading(false);
    } catch (err) {
      setError('Failed to fetch bookings. Please try again later.');
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'confirmed':
        return 'bg-green-500/20 text-green-400 border-green-500/30';
      case 'expired':
        return 'bg-red-500/20 text-red-400 border-red-500/30';
      case 'cancelled':
        return 'bg-gray-500/20 text-gray-400 border-gray-500/30';
      default:
        return 'bg-blue-500/20 text-blue-400 border-blue-500/30';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const BookingModal = ({ booking, onClose }) => (
    <div className="fixed inset-0 bg-black/80 backdrop-blur-sm flex items-center justify-center z-50 p-4">
      <div className="bg-gradient-to-br from-slate-800 to-slate-900 rounded-2xl max-w-md w-full p-6 border border-slate-700">
        <div className="flex justify-between items-center mb-6">
          <h3 className="text-2xl font-bold text-white">Ticket Details</h3>
          <button 
            onClick={onClose}
            className="text-gray-400 hover:text-white text-2xl"
          >
            ×
          </button>
        </div>
        
        <div className="space-y-4">
          <div className="text-center bg-white rounded-lg p-4 mb-4">
            <div className="text-4xl font-bold text-slate-900 mb-2">{booking.ticketCode}</div>
            <div className="text-sm text-slate-600">Show this code at the theater</div>
          </div>
          
          <div className="space-y-3">
            <div className="flex justify-between">
              <span className="text-gray-400">Movie:</span>
              <span className="text-white font-semibold">{booking.movieTitle}</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-400">Date:</span>
              <span className="text-white">{formatDate(booking.showDate)}</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-400">Time:</span>
              <span className="text-white">{booking.showTime}</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-400">Theater:</span>
              <span className="text-white">{booking.theater}</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-400">Seats:</span>
              <span className="text-white">{booking.seats.join(', ')}</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-400">Total:</span>
              <span className="text-green-400 font-bold">₹{booking.totalAmount}</span>
            </div>
          </div>
        </div>
        
        <button 
          onClick={onClose}
          className="w-full mt-6 bg-gradient-to-r from-purple-600 to-purple-700 text-white py-3 rounded-lg font-semibold hover:from-purple-700 hover:to-purple-800 transition-all duration-300"
        >
          Close
        </button>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-16 w-16 border-4 border-purple-500 border-t-transparent mx-auto mb-4"></div>
          <p className="text-white text-xl">Loading your bookings...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 flex items-center justify-center">
        <div className="text-center">
          <div className="text-6xl mb-4">❌</div>
          <p className="text-white text-xl mb-4">{error}</p>
          <button 
            onClick={fetchBookings}
            className="bg-gradient-to-r from-purple-600 to-purple-700 text-white px-6 py-3 rounded-lg hover:from-purple-700 hover:to-purple-800 transition-all duration-300"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900">
      {/* Header */}
      <header className="bg-black/20 backdrop-blur-sm border-b border-slate-800">
        <div className="max-w-7xl mx-auto px-4 py-8">
          <div className="flex items-center gap-4">
            <div className="text-4xl">🎫</div>
            <div>
              <h1 className="text-4xl font-bold text-white">My Bookings</h1>
              <p className="text-gray-300">View and manage your movie tickets</p>
            </div>
          </div>
        </div>
      </header>

      {/* Bookings List */}
      <main className="max-w-7xl mx-auto px-4 py-8">
        {bookings.length === 0 ? (
          <div className="text-center py-16">
            <div className="text-8xl mb-6">🎭</div>
            <h2 className="text-3xl font-bold text-white mb-4">No Bookings Found</h2>
            <p className="text-gray-400 mb-8">You haven't booked any movies yet.</p>
            <button className="bg-gradient-to-r from-red-600 to-red-700 text-white px-8 py-4 rounded-full font-semibold hover:from-red-700 hover:to-red-800 transition-all duration-300">
              Book Your First Movie
            </button>
          </div>
        ) : (
          <div className="space-y-6">
            {bookings.map((booking) => (
              <div
                key={booking.id}
                className="bg-gradient-to-r from-slate-800/50 to-slate-900/50 backdrop-blur-sm rounded-2xl border border-slate-700 hover:border-purple-500/50 transition-all duration-300 overflow-hidden"
              >
                <div className="flex flex-col lg:flex-row">
                  {/* Movie Poster */}
                  <div className="lg:w-32 lg:h-48 w-full h-40 flex-shrink-0">
                    <img
                      src={booking.posterUrl}
                      alt={booking.movieTitle}
                      className="w-full h-full object-cover"
                    />
                  </div>

                  {/* Booking Details */}
                  <div className="flex-1 p-6">
                    <div className="flex flex-col lg:flex-row justify-between items-start lg:items-center mb-4">
                      <div>
                        <h3 className="text-2xl font-bold text-white mb-2">{booking.movieTitle}</h3>
                        <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-semibold border ${getStatusColor(booking.status)}`}>
                          {booking.status.charAt(0).toUpperCase() + booking.status.slice(1)}
                        </div>
                      </div>
                      <div className="text-right mt-4 lg:mt-0">
                        <div className="text-3xl font-bold text-green-400">₹{booking.totalAmount}</div>
                        <div className="text-sm text-gray-400">Total Amount</div>
                      </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
                      <div>
                        <div className="text-sm text-gray-400">Show Date</div>
                        <div className="text-white font-semibold">{formatDate(booking.showDate)}</div>
                      </div>
                      <div>
                        <div className="text-sm text-gray-400">Show Time</div>
                        <div className="text-white font-semibold">{booking.showTime}</div>
                      </div>
                      <div>
                        <div className="text-sm text-gray-400">Theater</div>
                        <div className="text-white font-semibold">{booking.theater}</div>
                      </div>
                      <div>
                        <div className="text-sm text-gray-400">Seats</div>
                        <div className="text-white font-semibold">{booking.seats.join(', ')}</div>
                      </div>
                    </div>

                    <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
                      <div className="text-sm text-gray-400">
                        Booking Code: <span className="text-white font-mono">{booking.bookingCode}</span>
                        <br />
                        Booked on: {formatDate(booking.bookingDate)}
                      </div>
                      
                      <div className="flex gap-2">
                        {booking.status === 'confirmed' && (
                          <button
                            onClick={() => setSelectedBooking(booking)}
                            className="bg-gradient-to-r from-green-600 to-green-700 text-white px-6 py-2 rounded-lg font-semibold hover:from-green-700 hover:to-green-800 transition-all duration-300"
                          >
                            Show Ticket
                          </button>
                        )}
                        <button className="bg-gradient-to-r from-slate-600 to-slate-700 text-white px-6 py-2 rounded-lg font-semibold hover:from-slate-700 hover:to-slate-800 transition-all duration-300">
                          Details
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Summary Stats */}
        {bookings.length > 0 && (
          <div className="mt-12 grid grid-cols-1 md:grid-cols-3 gap-6">
            <div className="bg-gradient-to-br from-green-900/30 to-green-800/30 rounded-2xl p-6 border border-green-500/30">
              <div className="text-3xl font-bold text-green-400">{bookings.filter(b => b.status === 'confirmed').length}</div>
              <div className="text-green-300">Active Bookings</div>
            </div>
            <div className="bg-gradient-to-br from-blue-900/30 to-blue-800/30 rounded-2xl p-6 border border-blue-500/30">
              <div className="text-3xl font-bold text-blue-400">{bookings.length}</div>
              <div className="text-blue-300">Total Bookings</div>
            </div>
            <div className="bg-gradient-to-br from-purple-900/30 to-purple-800/30 rounded-2xl p-6 border border-purple-500/30">
              <div className="text-3xl font-bold text-purple-400">₹{bookings.reduce((sum, b) => sum + b.totalAmount, 0)}</div>
              <div className="text-purple-300">Total Spent</div>
            </div>
          </div>
        )}
      </main>

      {/* Ticket Modal */}
      {selectedBooking && (
        <BookingModal 
          booking={selectedBooking} 
          onClose={() => setSelectedBooking(null)} 
        />
      )}
    </div>
  );
}
