import React, { useState } from 'react';
import { Film, Calendar, Ticket, User, Star, Clock, MapPin, Search, Play } from 'lucide-react';

export default function Home() {
  const [activeTab, setActiveTab] = useState('home');
  const [selectedMovie, setSelectedMovie] = useState(null);
  const [bookings, setBookings] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  // Sample movie data
  const movies = [
    {
      id: 1,
      title: "Avatar: The Way of Water",
      genre: "Sci-Fi, Adventure",
      duration: "3h 12m",
      rating: 4.8,
      image: "https://images.unsplash.com/photo-1489599004830-b5de71e5c4d8?w=400&h=600&fit=crop",
      description: "Set more than a decade after the events of the first film, Avatar: The Way of Water begins to tell the story of the Sully family.",
      shows: [
        { id: 1, time: "10:00 AM", screen: "Screen 1", price: 250, seats: 45 },
        { id: 2, time: "2:30 PM", screen: "Screen 2", price: 300, seats: 32 },
        { id: 3, time: "7:00 PM", screen: "Screen 1", price: 350, seats: 28 }
      ]
    },
    {
      id: 2,
      title: "Top Gun: Maverick",
      genre: "Action, Drama",
      duration: "2h 17m",
      rating: 4.9,
      image: "https://images.unsplash.com/photo-1440404653325-ab127d49abc1?w=400&h=600&fit=crop",
      description: "After thirty years, Maverick is still pushing the envelope as a top naval aviator.",
      shows: [
        { id: 4, time: "11:30 AM", screen: "Screen 3", price: 280, seats: 38 },
        { id: 5, time: "4:00 PM", screen: "Screen 2", price: 320, seats: 25 },
        { id: 6, time: "8:30 PM", screen: "Screen 3", price: 380, seats: 15 }
      ]
    },
    {
      id: 3,
      title: "The Batman",
      genre: "Action, Crime",
      duration: "2h 56m",
      rating: 4.7,
      image: "https://images.unsplash.com/photo-1635805737707-575885ab0820?w=400&h=600&fit=crop",
      description: "When a sadistic serial killer begins murdering key political figures in Gotham, Batman is forced to investigate.",
      shows: [
        { id: 7, time: "1:00 PM", screen: "Screen 4", price: 270, seats: 42 },
        { id: 8, time: "6:00 PM", screen: "Screen 4", price: 330, seats: 18 },
        { id: 9, time: "9:30 PM", screen: "Screen 1", price: 400, seats: 8 }
      ]
    }
  ];

  const filteredMovies = movies.filter(movie =>
    movie.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    movie.genre.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const bookTicket = (movie, show) => {
    const newBooking = {
      id: bookings.length + 1,
      movieTitle: movie.title,
      showTime: show.time,
      screen: show.screen,
      price: show.price,
      bookingDate: new Date().toLocaleDateString(),
      ticketId: `TKT${Math.random().toString(36).substr(2, 9).toUpperCase()}`
    };
    setBookings([...bookings, newBooking]);
    alert(`Booking confirmed! Your ticket ID is ${newBooking.ticketId}`);
  };

  const Navigation = () => (
    <nav className="bg-gradient-to-r from-purple-900 to-indigo-900 text-white p-4 shadow-2xl">
      <div className="max-w-7xl mx-auto flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <Film className="w-8 h-8 text-yellow-400" />
          <h1 className="text-2xl font-bold">CinePlex</h1>
        </div>
        <div className="flex space-x-6">
          {[
            { id: 'home', label: 'Home', icon: Film },
            { id: 'movies', label: 'Movies', icon: Play },
            { id: 'shows', label: 'Shows', icon: Calendar },
            { id: 'bookings', label: 'My Bookings', icon: Ticket }
          ].map(({ id, label, icon: Icon }) => (
            <button
              key={id}
              onClick={() => setActiveTab(id)}
              className={`flex items-center space-x-1 px-4 py-2 rounded-lg transition-all ${
                activeTab === id 
                  ? 'bg-white text-purple-900 shadow-lg' 
                  : 'hover:bg-purple-800'
              }`}
            >
              <Icon className="w-4 h-4" />
              <span>{label}</span>
            </button>
          ))}
        </div>
      </div>
    </nav>
  );

  const HomePage = () => (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900">
      {/* Hero Section */}
      <div className="relative overflow-hidden">
        <div className="absolute inset-0 bg-black opacity-60"></div>
        <div 
          className="h-96 bg-cover bg-center flex items-center justify-center"
          style={{
            backgroundImage: "url('https://images.unsplash.com/photo-1489599004830-b5de71e5c4d8?w=1200&h=400&fit=crop')"
          }}
        >
          <div className="relative z-10 text-center text-white px-4">
            <h2 className="text-5xl font-bold mb-4 animate-pulse">Welcome to CinePlex</h2>
            <p className="text-xl mb-8 max-w-2xl mx-auto">
              Experience cinema like never before. Book your favorite movies instantly and enjoy the ultimate entertainment experience.
            </p>
            <button
              onClick={() => setActiveTab('movies')}
              className="bg-gradient-to-r from-yellow-400 to-orange-500 text-black px-8 py-3 rounded-full font-semibold text-lg hover:from-yellow-500 hover:to-orange-600 transition-all transform hover:scale-105 shadow-xl"
            >
              Book Now
            </button>
          </div>
        </div>
      </div>

      {/* Features Section */}
      <div className="py-16 px-4">
        <div className="max-w-7xl mx-auto">
          <h3 className="text-3xl font-bold text-center text-white mb-12">Why Choose CinePlex?</h3>
          <div className="grid md:grid-cols-3 gap-8">
            {[
              {
                icon: Ticket,
                title: "Instant Booking",
                description: "Book your tickets in seconds with our lightning-fast booking system"
              },
              {
                icon: Star,
                title: "Premium Experience",
                description: "Enjoy the best movie experience with state-of-the-art technology"
              },
              {
                icon: MapPin,
                title: "Multiple Locations",
                description: "Find us at convenient locations across the city"
              }
            ].map((feature, index) => (
              <div key={index} className="bg-white/10 backdrop-blur-lg rounded-xl p-8 text-center hover:bg-white/20 transition-all">
                <feature.icon className="w-16 h-16 text-yellow-400 mx-auto mb-4" />
                <h4 className="text-xl font-semibold text-white mb-3">{feature.title}</h4>
                <p className="text-gray-300">{feature.description}</p>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* How It Works */}
      <div className="py-16 px-4 bg-black/30">
        <div className="max-w-7xl mx-auto">
          <h3 className="text-3xl font-bold text-center text-white mb-12">How It Works</h3>
          <div className="grid md:grid-cols-4 gap-6">
            {[
              "Browse Movies",
              "Select Show",
              "Book Seats",
              "Get Tickets"
            ].map((step, index) => (
              <div key={index} className="text-center">
                <div className="w-16 h-16 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full flex items-center justify-center text-white text-2xl font-bold mx-auto mb-4">
                  {index + 1}
                </div>
                <h4 className="text-lg font-semibold text-white">{step}</h4>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );

  const MoviesPage = () => (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 to-purple-900 p-6">
      <div className="max-w-7xl mx-auto">
        <div className="mb-8">
          <h2 className="text-3xl font-bold text-white mb-4">Now Showing</h2>
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
            <input
              type="text"
              placeholder="Search movies..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-3 bg-white/10 backdrop-blur-lg rounded-lg text-white placeholder-gray-400 border border-white/20 focus:border-purple-400 focus:outline-none"
            />
          </div>
        </div>

        <div className="grid md:grid-cols-2 lg:grid-cols-3 gap-8">
          {filteredMovies.map((movie) => (
            <div key={movie.id} className="bg-white/10 backdrop-blur-lg rounded-xl overflow-hidden hover:bg-white/20 transition-all hover:scale-105">
              <img
                src={movie.image}
                alt={movie.title}
                className="w-full h-64 object-cover"
              />
              <div className="p-6">
                <h3 className="text-xl font-bold text-white mb-2">{movie.title}</h3>
                <div className="flex items-center space-x-4 text-sm text-gray-300 mb-3">
                  <span>{movie.genre}</span>
                  <span className="flex items-center">
                    <Clock className="w-4 h-4 mr-1" />
                    {movie.duration}
                  </span>
                  <span className="flex items-center">
                    <Star className="w-4 h-4 mr-1 text-yellow-400" />
                    {movie.rating}
                  </span>
                </div>
                <p className="text-gray-300 text-sm mb-4">{movie.description}</p>
                <button
                  onClick={() => setSelectedMovie(movie)}
                  className="w-full bg-gradient-to-r from-purple-600 to-pink-600 text-white py-2 rounded-lg hover:from-purple-700 hover:to-pink-700 transition-all"
                >
                  View Shows
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const ShowsPage = () => (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 to-purple-900 p-6">
      <div className="max-w-7xl mx-auto">
        <h2 className="text-3xl font-bold text-white mb-8">All Shows</h2>
        <div className="space-y-6">
          {movies.map((movie) => (
            <div key={movie.id} className="bg-white/10 backdrop-blur-lg rounded-xl p-6">
              <div className="flex items-center space-x-4 mb-4">
                <img
                  src={movie.image}
                  alt={movie.title}
                  className="w-20 h-28 object-cover rounded-lg"
                />
                <div>
                  <h3 className="text-xl font-bold text-white">{movie.title}</h3>
                  <p className="text-gray-300">{movie.genre} • {movie.duration}</p>
                </div>
              </div>
              <div className="grid md:grid-cols-3 gap-4">
                {movie.shows.map((show) => (
                  <div key={show.id} className="bg-black/30 rounded-lg p-4">
                    <div className="flex justify-between items-center mb-2">
                      <span className="text-yellow-400 font-semibold">{show.time}</span>
                      <span className="text-white">₹{show.price}</span>
                    </div>
                    <div className="text-sm text-gray-300 mb-3">
                      {show.screen} • {show.seats} seats available
                    </div>
                    <button
                      onClick={() => bookTicket(movie, show)}
                      className="w-full bg-gradient-to-r from-green-600 to-blue-600 text-white py-2 rounded hover:from-green-700 hover:to-blue-700 transition-all"
                    >
                      Book Now
                    </button>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const BookingsPage = () => (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 to-purple-900 p-6">
      <div className="max-w-4xl mx-auto">
        <h2 className="text-3xl font-bold text-white mb-8">My Bookings</h2>
        {bookings.length === 0 ? (
          <div className="text-center py-12">
            <Ticket className="w-24 h-24 text-gray-400 mx-auto mb-4" />
            <h3 className="text-xl text-gray-400 mb-2">No bookings yet</h3>
            <p className="text-gray-500 mb-6">Book your first movie to see your tickets here</p>
            <button
              onClick={() => setActiveTab('movies')}
              className="bg-gradient-to-r from-purple-600 to-pink-600 text-white px-6 py-3 rounded-lg hover:from-purple-700 hover:to-pink-700 transition-all"
            >
              Browse Movies
            </button>
          </div>
        ) : (
          <div className="space-y-4">
            {bookings.map((booking) => (
              <div key={booking.id} className="bg-white/10 backdrop-blur-lg rounded-xl p-6">
                <div className="flex justify-between items-start">
                  <div>
                    <h3 className="text-xl font-bold text-white mb-2">{booking.movieTitle}</h3>
                    <div className="space-y-1 text-gray-300">
                      <p>Show Time: {booking.showTime}</p>
                      <p>Screen: {booking.screen}</p>
                      <p>Booking Date: {booking.bookingDate}</p>
                    </div>
                  </div>
                  <div className="text-right">
                    <p className="text-2xl font-bold text-green-400">₹{booking.price}</p>
                    <p className="text-sm text-gray-400">Ticket ID: {booking.ticketId}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );

  // Movie Detail Modal
  const MovieModal = () => (
    selectedMovie && (
      <div className="fixed inset-0 bg-black/80 flex items-center justify-center p-4 z-50">
        <div className="bg-white/10 backdrop-blur-lg rounded-xl max-w-2xl w-full max-h-[90vh] overflow-y-auto">
          <div className="p-6">
            <div className="flex justify-between items-start mb-4">
              <h3 className="text-2xl font-bold text-white">{selectedMovie.title}</h3>
              <button
                onClick={() => setSelectedMovie(null)}
                className="text-white hover:text-red-400 text-2xl"
              >
                ×
              </button>
            </div>
            <img
              src={selectedMovie.image}
              alt={selectedMovie.title}
              className="w-full h-64 object-cover rounded-lg mb-4"
            />
            <p className="text-gray-300 mb-6">{selectedMovie.description}</p>
            <h4 className="text-lg font-semibold text-white mb-4">Available Shows</h4>
            <div className="space-y-3">
              {selectedMovie.shows.map((show) => (
                <div key={show.id} className="flex justify-between items-center bg-black/30 rounded-lg p-4">
                  <div>
                    <span className="text-yellow-400 font-semibold">{show.time}</span>
                    <span className="text-gray-300 ml-4">{show.screen}</span>
                  </div>
                  <div className="flex items-center space-x-4">
                    <span className="text-white">₹{show.price}</span>
                    <span className="text-sm text-gray-400">{show.seats} seats</span>
                    <button
                      onClick={() => {
                        bookTicket(selectedMovie, show);
                        setSelectedMovie(null);
                      }}
                      className="bg-gradient-to-r from-green-600 to-blue-600 text-white px-4 py-2 rounded hover:from-green-700 hover:to-blue-700 transition-all"
                    >
                      Book
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    )
  );

  return (
    <div className="min-h-screen bg-gray-900">
      <Navigation />
      {activeTab === 'home' && <HomePage />}
      {activeTab === 'movies' && <MoviesPage />}
      {activeTab === 'shows' && <ShowsPage />}
      {activeTab === 'bookings' && <BookingsPage />}
      <MovieModal />
    </div>
  );
}
