import React, { useState } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';

export default function Bookings() {
  const { showId } = useParams(); // route param should match backend `show_id`
  const [userId, setUserId] = useState('');
  const [seatsBooked, setSeatsBooked] = useState(1);
  const [result, setResult] = useState(null);
  const [error, setError] = useState(null);

  const handleBook = async (e) => {
    e.preventDefault();
    setError(null);
    setResult(null);

    try {
      const res = await axios.post('/api/bookings', {
        user_id: Number(userId),
        show_id: Number(showId),
        seats_booked: Number(seatsBooked)
      });
      setResult(res.data);
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.error || 'Booking failed');
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>Book Tickets</h1>
      <form onSubmit={handleBook}>
        <div>
          <label>User ID (temp): </label>
          <input
            value={userId}
            onChange={(e) => setUserId(e.target.value)}
            placeholder="1"
            required
          />
        </div>
        <div>
          <label>Seats: </label>
          <input
            type="number"
            value={seatsBooked}
            min="1"
            onChange={(e) => setSeatsBooked(e.target.value)}
            required
          />
        </div>
        <button type="submit" style={{ marginTop: 10 }}>
          Confirm Booking
        </button>
      </form>

      {result && (
        <div style={{ marginTop: 20, padding: 10, border: '1px solid green' }}>
          <h3>Booking Confirmed!</h3>
          <p>Booking ID: {result.booking_id}</p>
          <p>m-Ticket Code: <strong>{result.booking_code}</strong></p>
          <p>Total Amount: ₹{result.total_amount}</p>
          <p>Status: {result.status}</p>
        </div>
      )}

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <div style={{ marginTop: 20 }}>
        <Link to={`/schedules/${showId}`}>← Back to Schedule</Link>
      </div>
    </div>
  );
}
